mapper.insert:
insert into #{table, jdbcType=VARCHAR} (

    #{column, jdbcType=VARCHAR}

) values (

    #{value, jdbcType=VARCHAR}

)

mapper.delete:
update #{table, jdbcType=VARCHAR} set

    #{value.key, jdbcType=VARCHAR} = #{value.value, jdbcType=VARCHAR}

where id = #{id, jdbcType=BIGINT}
and version_number = #{version_number, jdbcType=INTEGER}

delete from #{table, jdbcType=VARCHAR}
where id in (

    #{id, jdbcType=BIGINT}

) and lang_code = #{langCode, jdbcType=VARCHAR}

mapper.select:
select * from #{table, jdbcType=VARCHAR}
where id in (

    #{id, jdbcType=BIGINT}

) and lang_code = #{langCode, jdbcType=VARCHAR}
