mapper.insert:
insert into ${table} ( id, lang_code, created_date, created_by, last_updated_date, last_updated_by, version_number
,${column}
) values ( #{id, jdbcType=BIGINT}, #{langCode, jdbcType=VARCHAR}, #{createdDate, jdbcType=TIMESTAMP}, #{createdBy, jdbcType=BIGINT}, #{lastUpdatedDate, jdbcType=TIMESTAMP}, #{lastUpdatedBy, jdbcType=BIGINT}, #{versionNumber, jdbcType=INTEGER}
,#{value, jdbcType=VARCHAR}
)

mapper.delete:
update ${table} set
last_updated_date = #{lastUpdatedDate, jdbcType=TIMESTAMP},
last_updated_by = #{lastUpdatedBy, jdbcType=BIGINT},
version_number = version_number + 1

    ,${column} = #{value, jdbcType=VARCHAR}

where id = #{id, jdbcType=BIGINT}
and lang_code = #{langCode, jdbcType=VARCHAR}

    and version_number = #{versionNumber, jdbcType=INTEGER}

delete from ${table}
where id in

    #{id, jdbcType=BIGINT}


    and lang_code = #{langCode, jdbcType=VARCHAR}

mapper.select:
select * from ${table}
where id in

    #{id, jdbcType=BIGINT}


    and lang_code = #{langCode, jdbcType=VARCHAR}
